package ar.edu.unlam.tallerweb;

public class Circulo {
	Double pi = 3.14;
	Double resultado;
	
	public Double perimetroDelCirculo(double radio) {
		return resultado = 2*pi*radio;
	}

	public Double areaDelCirculo(double radio) {
		// TODO Auto-generated method stub
		return resultado = pi*radio*radio;
	}

}
